import React, { useState } from 'react';
import styles from "./Formulario.css"

function Formulario({ onCadastro }) {
  const [nome, setNome] = useState('');
  const [telefone, setTelefone] = useState('');
  const [tipo, setTipo] = useState('');
  const [pago, setPago] = useState(''); 
  const [foto, setFoto] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validar e salvar os dados
    if (nome && telefone && tipo && pago) {
      onCadastro({ nome, telefone, tipo, pago });
      setNome('');
      setTelefone('');
      setTipo('');
      setPago('');
    }
  };

  return (
    <div className='container'>
    <h1>Cadastro de Pessoas</h1>
    <form className='form-container' onSubmit={handleSubmit}>
        <h3>Nome</h3>
      <input
      className='input-data'
        type="text"
        placeholder="Nome"
        value={nome}
        onChange={(e) => setNome(e.target.value)}
      />
      <h3>Telefone</h3>
      <input
        className='input-data'
        type="tel"
        placeholder="Telefone"
        value={telefone}
        onChange={(e) => setTelefone(e.target.value)}
      />
      <div>
        <label className='label-input-radio'>
          <input
            type="radio"
            value="Egresso"
            checked={tipo === 'Egresso'}
            onChange={() => setTipo('Egresso')}
          />
          Egresso
        </label>
        <label className='label-input-radio'>
          <input
            type="radio"
            value="Convidado"
            checked={tipo === 'Convidado'}
            onChange={() => setTipo('Convidado')}
          />
          Convidado
        </label>
      </div>
      <div>
        <h3>Pago</h3>
        <label className='label-input-radio'>
          <input
            type="radio"
            value="Sim"
            checked={pago === 'Sim'}
            onChange={() => setPago('Sim')}
          />
          Sim
        </label>
        <label className='label-input-radio'>
          <input
            type="radio"
            value="Nao"
            checked={pago === 'Nao'}
            onChange={() => setPago('Nao')}
          />
          NÃ£o
        </label>
        <h3>Foto</h3>
        <label>
            <input type="url" placeholder='URL' value={foto}
        onChange={(e) => setFoto(e.target.value)}/>
        </label>
      </div>
      <button className='buttonEnviar' type="submit">Cadastrar</button>
    </form>
    </div>
  );
}

export default Formulario;
