import React, { useState } from 'react';
import styles from "./Formulario.css"

function Formulario() {

  async function handleProduct(formData) {
    try {
      const response = await fetch('http://localhost:3001/products', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
  
      if (!response.ok) {
        throw new Error('Erro ao enviar produto');
      }
      console.log('Produto enviado com sucesso!');
    } catch (error) {
      console.error('Erro ao enviar produto:', error.message);
    }
  }


    const [formData, setFormData] = useState({
      name: '',
      price: '',
      description: '',
      photo_url: ''
    });
  
    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData({ ...formData, [name]: value });
    };
  
    const handleSubmit = async (e) => {
      e.preventDefault();
      try {
        await handleProduct(formData);
        // Lógica adicional após o envio, se necessário
      } catch (error) {
        console.error('Erro ao enviar produto:', error);
      }
    };
    
  return (
    <div className='container'>

    <form className='form-container' onSubmit={handleSubmit}>
      <h1>Novo Produto</h1>
      <h3>Nome</h3>
      <input className='input-data' type="text" placeholder="Nome" name="name" value={formData.name} onChange={handleChange}/>

      <h3>Preço</h3>
      <input className='input-data' type="text" placeholder="Preço" name="price" value={formData.price}
          onChange={handleChange}/>

      <h3>Descrição</h3>
      <input className='input-data' type="text"  placeholder="Descricao"  name="description" value={formData.description}
          onChange={handleChange}/>

      <h3>Foto</h3>
      <input className='input-data' type="text" placeholder="URL Foto" name="photo_url" value={formData.photo_url}
          onChange={handleChange}/>
      
      
      
      <button className='buttonEnviar' type="submit">Cadastrar</button>
    </form>
    </div>
  );
}

export default Formulario;
