import React, { useState } from 'react';

function FormularioCadastro({ onCadastro }) {
  const [nome, setNome] = useState('');
  const [telefone, setTelefone] = useState('');
  const [egresso, setEgresso] = useState('');
  const [convidado, setConvidado] = useState('');
  const [pago, setPago] = useState('');
  const [naopago, setNaoPago] = useState('');  
  const handleSubmit = (e) => {
    e.preventDefault();
    // Validar e salvar os dados
    if (nome && telefone) {
      onCadastro({ nome, telefone });
      setNome('');
      setTelefone('');
      setEgresso('');
      setConvidado('');
      setPago('');
      setNaoPago('');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Nome"
        value={nome}
        onChange={(e) => setNome(e.target.value)}
      />
      <input
        type="tel"
        placeholder="Telefone"
        value={telefone}
        onChange={(e) => setTelefone(e.target.value)}
      />
      
        <div>
                    <input type="radio" id="egresso" name="aluno" value={egresso} onChange={(e) => setEgresso(e.target.value)}/>
                    <label for="egresso">Egresso</label>
                    <input type="radio" id="convidado" name="aluno" value={convidado} onChange={(e) => setConvidado(e.target.value)}/>
                    <label for="convidado">Convidado</label>
        </div>

                <div>
                    <p>Pago</p>
                    <input type="radio" id="pago" name="pagamento" value={pago} onChange={(e) => setPago(e.target.value)}/>
                    <label for="pago">Sim</label>
                    <input type="radio" id="nao-pago" name="pagamento" value={naopago} onChange={(e) => setNaoPago(e.target.value)}/>
                    <label for="nao-pago">NÃ£o</label>
                </div>

                <label for="site">Foto</label>
	            <input type="url" id="site" name="site" required placeholder="URL"></input>
      <button type="submit">Cadastrar</button>
    </form>
  );
}

export default FormularioCadastro;
