import React, { useState } from 'react';

function FormularioCadastro({ onCadastro }) {
  const [nome, setNome] = useState('');
  const [telefone, setTelefone] = useState('');
  const [egresso, setEgresso] = useState(false);
  const [convidado, setConvidado] = useState(false);
  const [pago, setPago] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validar e salvar os dados
    if (nome && telefone) {
      onCadastro({ nome, telefone, egresso, convidado, pago });
      setNome('');
      setTelefone('');
      setEgresso(false);
      setConvidado(false);
      setPago(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Nome"
        value={nome}
        onChange={(e) => setNome(e.target.value)}
      />
      <input
        type="tel"
        placeholder="Telefone"
        value={telefone}
        onChange={(e) => setTelefone(e.target.value)}
      />
      
      <div>
        <label>
          <input type="checkbox" checked={egresso} onChange={() => setEgresso(!egresso)} />
          Egresso
        </label>
      </div>

      <div>
        <label>
          <input type="checkbox" checked={convidado} onChange={() => setConvidado(!convidado)} />
          Convidado
        </label>
      </div>

      <div>
        <label>
          <input type="checkbox" checked={pago} onChange={() => setPago(!pago)} />
          Pago
        </label>
      </div>

      <button type="submit">Cadastrar</button>
    </form>
  );
}

export default FormularioCadastro;
